INSERT INTO szweb.community (id, hit, title, update_time, content, type, update_user) VALUES (1, 1, '冬季小学期', '2019-04-25 21:01:40', '冬季小学期是北京理工大学推出的创新人才培养模式，时间安排在每学年第一学期末的三至四周，邀请院士、长江学者、国家教学名师等国内外知名专家学者前来授课，使在校学生享受到国内外优质教学资源，开拓了学生的视野，促进了学校教学质量的提高，搭建了高质量的人才培养平台。', 'hot', 1);
INSERT INTO szweb.community (id, hit, title, update_time, content, type, update_user) VALUES (2, 0, '订单式人才培养', '2019-03-09 00:00:00', '订单式人才培养是北京理工大学积极探索的校企合作的人才培养模式。应用科技学院以理论学习、专业技术学习和业务拓展培训相结合，为企业定向培养输送人才。以应用型管理人才教育为目标定位，以在校理论学习和到企业待岗实习相结合的方式，为企业培养高层次的管理人才。', 'hot', 1);
INSERT INTO szweb.community (id, hit, title, update_time, content, type, update_user) VALUES (3, 6, '学科建设', '2019-04-22 14:58:58', '学校牢记习近平总书记“4·13”重要讲话精神中关于“要支持北京理工大学创建世界一流学科”的殷切嘱托，着眼于服务国家重大战略，坚持“突出特色、重点建设、全面发展”的原则，突出“热带、海洋、旅游、特区”四大特色，坚持“支撑引领、特色取胜，高位嫁接、开放创新”的学科建设总体思路，构建了学科建设目标和国家战略、区域经济社会发展需求相适应的学科专业体系。', 'research', 1);
INSERT INTO szweb.community (id, hit, title, update_time, content, type, update_user) VALUES (4, 0, '科研成果', '2019-03-09 00:00:00', '2008年以来，北京理工大学科研实力大幅提升，产生了一批科技含量高、社会影响大的标志性成果。先后获得教育部高等学校科学研究优秀成果奖（人文社科）、北京市科技进步奖、北京市科技成果转化奖、北京市社会科学优秀成果奖等省部级以上奖励。', 'research', 1);
